function writeVRMLpreamble( fid, bgcolor )
    if nargin < 2
        bgcolor = [ 0.15 0.3 0.36 ];
    end
    fprintf( fid, ...
      [ '#VRML V2.0 utf8\n' ...
        '\n', ...
        '# Generated by GFtbox at ', datestr(clock), '\n', ...
        '\n', ...
        '\n', ...
        'NavigationInfo {\n', ...
        '  avatarSize    [ 0.15, 1.53, 0.75 ]\n', ...
        '  headlight TRUE\n', ...
        '  speed 0.5\n', ...
        '  type [ "EXAMINE" "ANY" ]\n', ...
        '}\n', ...
        '\n', ...
        'Background {\n', ...
        '    skyColor [ %g %g %g ]\n', ...
        '}\n', ...
        '\n' ], bgcolor );
end
